import NoteContext from '../context/NoteContext';
import { useContext, useState ,useMEm, useMemo} from 'react';


const useNote = () => {

    //useContext ile verileri bir çek 

    const { state, dispatch } = useContext(NoteContext);
    const [loading, setLoading] = useState(true);

    //function is a alldata fetch
    const AllNoteMemorization = useMemo(getNotes,)
    const getNotes = async () => {
        // console.log(state,dispatch);

        try {
            const response = await fetch('http://localhost:8080/api/not/');
            if (!response.ok) {

                throw new Error('Failed to fetch data');

            }
            const data = await response.json();
            console.log(data);
            dispatch({
                type: 'GET_NOTES',
                payload: data.notes,
            });
            setLoading(false);
        } catch (error) {
            console.log(error);

        }
    }
    //delete note 
    const deleteNote = async (id) => {
        const url = `http://localhost:8080/api/not/${id}`;
        try {

            const response = await fetch(url,{
                method: 'DELETE',
            });
            if (!response.ok) {
                throw new Error('Failed to delete note');
            }
            dispatch({
                type: 'DELETE_NOTE',
                payload: id,
            });
            console.log(`${id} deleted`);
            
        } catch (error) {
            console.log(error);
        }
    }

    //add note 
    const addNote = async (title, content) => {
        try {
            const response = await fetch('http://localhost:8080/api/not',{
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ title, content }),
            });
            if (!response.ok) {
                throw new Error('Failed to add note');
            }
            const data = await response.json();
            dispatch({
                type: 'ADD_NOTE',
                payload: data.note,
            });
        } catch (error) {
            console.log(error);
            
        }
    }


    return {
        Notes: state.notes,
        getNotes,
        Loading: loading,
        deleteNote,
        addNote
    }





}

export default useNote
