import { createContext, useReducer } from "react";

const NoteContent = createContext();
//note için reducer tanımlama 

const initalState = {
    notes: [],
}


const noteReducer = (state, action) => {
    switch (action.type) {
        case 'ADD_NOTE':
            return { ...state, notes: [...state.notes, action.payload] }
        case 'DELETE_NOTE':
            return { ...state, notes: state.notes.filter((note) => note.id !== action.payload) }
        default:
            return state;
    }
}



const NoteContext = ({ children }) => {
    const [state, dispatch] = useReducer(noteReducer, initalState);
    return (
        <NoteContent.Provider value={{state,dispatch}}>
            {children}
        </NoteContent.Provider>
    )
}

export default NoteContext
