import React, { useEffect, useState } from 'react'
import { Col, Row, Container, Input, Label, Button } from 'reactstrap'

import useAuth from '../hooks/useAuth';
import { Navigate } from 'react-router';

const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const { Login, AuthState } = useAuth();
    console.log(AuthState.isAuthenticated);
    const GirisYap =() => {
        Login(email, password)
        setEmail("")
        setPassword("")
    }
    useEffect(() => {
        if (AuthState.isAuthenticated) {
            return <Navigate to='/' />  // giriş yapıldıysa anasayfaya yönlendir
        }
    }, [])
    return (
        <Container >
            <Row className='mb-4'>
                <Col md={{ size: 6, offset: 3 }}>
                    <h1>Login</h1>
                </Col>
            </Row>
            <Row>
                <Col md={{ size: 6, offset: 3 }} className='mb-3'>
                    <Label>Email </Label>
                    <Input className='input_login' placeholder="Lütfen Kullanıcı Adını Girin ! "
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}

                    ></Input>
                </Col>
                <Col md={{ size: 6, offset: 3 }} className='mb-3'>
                    <Label>Şifre </Label>
                    <Input className="input_login" placeholder="Lütfen Kullanıcı Adını Girin ! "
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}

                    ></Input>
                </Col>
                <Col md={{ size: 6, offset: 3 }} className='mb-3'>
                    <Button className='btn-login'
                        onClick={GirisYap}
                    >Giriş Yap </Button>
                </Col>
            </Row>
        </Container>
    )
}

export default Login
